import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const ExamSelection = () => {
  const [exams, setExams] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // Fetch exams when the component mounts
  useEffect(() => {
    const fetchExams = async () => {
      try {
        const response = await axios.get("http://localhost:3001/exams", { withCredentials: true });
        setExams(response.data.exams);
        setLoading(false); // Stop loading once data is fetched
      } catch (err) {
        console.error("Error fetching exams:", err.response?.data || err.message);
        setError("Failed to load exams. Please try again later.");
        setLoading(false);
      }
    };

    fetchExams();
  }, []);

  const handleTakeExam = (examId) => {
    navigate(`/guideline/${examId}`); // Navigate to guidelines page for the exam
  };

  const handleViewResults = (examId) => {
    navigate(`/results`, { state: { examId } }); // Navigate to results page with examId
  };

  if (loading) {
    return <p>Loading exams...</p>; // Show a loading message while fetching exams
  }

  if (error) {
    return <p className="text-danger">{error}</p>; // Display error message if fetch fails
  }

  return (
    <div className="container mt-5">
      <h1 className="mb-4">Available Exams</h1>
      {exams.map((exam) => (
        <div key={exam.id} className="card mb-3 p-3 shadow">
          <h5>{exam.name}</h5>
          <p>{exam.description}</p>

          {/* Handle different button states based on exam status */}
          {exam.blocked ? (
            <button className="btn btn-danger" disabled>
              Blocked
            </button>
          ) : exam.completed ? (
            <button className="btn btn-secondary" onClick={() => handleViewResults(exam.id)}>
              View Results
            </button>
          ) : (
            <button className="btn btn-primary" onClick={() => handleTakeExam(exam.id)}>
              Take Test
            </button>
          )}
        </div>
      ))}
    </div>
  );
};

export default ExamSelection;
