import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap-icons/font/bootstrap-icons.css";
import { Dropdown } from "bootstrap";

function SignIn() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [theme, setTheme] = useState(localStorage.getItem("theme") || "light");

  useEffect(() => {
    document.documentElement.setAttribute("data-bs-theme", theme);
    localStorage.setItem("theme", theme);

    const dropdownElement = document.getElementById("bd-theme");
    if (dropdownElement) {
      new Dropdown(dropdownElement);
    }
  }, [theme]);

  const handleThemeChange = (selectedTheme) => {
    setTheme(selectedTheme);
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setError("");

    try {
      const response = await fetch("http://localhost:3001/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ username: email, password }),
      });

      const data = await response.json();
      if (!response.ok) throw new Error(data.error);

      alert("Login Successful!");
      console.log("User:", data.user);
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className="container vh-100 d-flex justify-content-center align-items-center">
      <div className="card p-4 shadow-lg" style={{ width: "100%", maxWidth: "400px" }}>
        <form className="text-center" onSubmit={handleLogin}>
          <img
            className="mb-4"
            src="https://getbootstrap.com/docs/5.3/assets/brand/bootstrap-logo.svg"
            alt="Bootstrap Logo"
            width="72"
            height="57"
          />
          <h1 className="h3 mb-3 fw-normal">Please sign in</h1>

          <div className="form-floating mb-2">
            <input
              type="email"
              className="form-control"
              id="floatingInput"
              placeholder="Email address"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <label htmlFor="floatingInput">Email address</label>
          </div>

          <div className="form-floating mb-2">
            <input
              type="password"
              className="form-control"
              id="floatingPassword"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <label htmlFor="floatingPassword">Password</label>
          </div>

          <div className="form-check text-start my-2">
            <input className="form-check-input" type="checkbox" id="flexCheckDefault" />
            <label className="form-check-label" htmlFor="flexCheckDefault">
              Remember me
            </label>
          </div>

          {error && <p className="text-danger">{error}</p>}

          <button className="btn btn-primary w-100 py-2 mt-2" type="submit">
            Sign in
          </button>
        </form>
      </div>

      {/* Theme Toggle Dropdown */}
      <div className="dropdown position-fixed bottom-0 end-0 mb-3 me-3">
        <button
          className="btn btn-bd-primary py-2 dropdown-toggle d-flex align-items-center"
          id="bd-theme"
          type="button"
          data-bs-toggle="dropdown"
          aria-expanded="false"
        >
          <i className={`bi bi-${theme === "dark" ? "moon-stars-fill" : theme === "auto" ? "circle-half" : "sun-fill"} theme-icon-active`}></i>
        </button>
        <ul className="dropdown-menu dropdown-menu-end shadow">
          <li>
            <button type="button" className={`dropdown-item d-flex align-items-center ${theme === "light" ? "active" : ""}`} onClick={() => handleThemeChange("light")}>
              <i className="bi bi-sun-fill me-2 opacity-50"></i> Light
            </button>
          </li>
          <li>
            <button type="button" className={`dropdown-item d-flex align-items-center ${theme === "dark" ? "active" : ""}`} onClick={() => handleThemeChange("dark")}>
              <i className="bi bi-moon-stars-fill me-2 opacity-50"></i> Dark
            </button>
          </li>
          <li>
            <button type="button" className={`dropdown-item d-flex align-items-center ${theme === "auto" ? "active" : ""}`} onClick={() => handleThemeChange("auto")}>
              <i className="bi bi-circle-half me-2 opacity-50"></i> Auto
            </button>
          </li>
        </ul>
      </div>
    </div>
  );
}

export default SignIn;
