import React, { useEffect, useState } from "react";
import axios from "axios";
import CreateQuestion from "./CreateQuestion";
import GenerateQuestions from "./GenerateQuestions";
import { io } from "socket.io-client";

const DashboardHome = ({ user, handleCreateExam, examName, setExamName, exams }) => {
  const [totalUsers, setTotalUsers] = useState(0);
  const [riskAlerts, setRiskAlerts] = useState(0);
  const [totalExams, setTotalExams] = useState(0);

  useEffect(() => {
    const socket = io("http://localhost:3001");

    // Listen for WebSocket events to update the dashboard dynamically
    socket.on("dashboard_update", (data) => {
      switch (data.type) {
        case "active_users":
          fetchActiveUsers(); // Fetch updated active users count
          break;
        case "risk_alerts":
          fetchRiskAlerts(); // Fetch updated risk alerts count
          break;
        case "total_exams":
          fetchTotalExams(); // Fetch updated total exams count
          break;
        default:
          console.warn("Unknown dashboard update type:", data.type);
      }
    });

    return () => socket.disconnect(); // Cleanup the WebSocket connection on unmount
  }, []);

  // Fetch initial dashboard data when the component mounts
  useEffect(() => {
    const fetchDashboardData = async () => {
      await fetchActiveUsers();
      await fetchRiskAlerts();
      await fetchTotalExams();
    };

    fetchDashboardData();
  }, []);

  // Fetch total active users count
  const fetchActiveUsers = async () => {
    try {
      const response = await axios.get("http://localhost:3001/active-users", { withCredentials: true });
      setTotalUsers(response.data.activeUsers);
    } catch (error) {
      console.error("Error fetching active users:", error);
    }
  };

  // Fetch total risk alerts count
  const fetchRiskAlerts = async () => {
    try {
      const response = await axios.get("http://localhost:3001/risk-alerts", { withCredentials: true });
      setRiskAlerts(response.data.riskAlerts);
    } catch (error) {
      console.error("Error fetching risk alerts:", error);
    }
  };

  // Fetch total exams count
  const fetchTotalExams = async () => {
    try {
      const response = await axios.get("http://localhost:3001/total-exams", { withCredentials: true });
      setTotalExams(response.data.totalExams);
    } catch (error) {
      console.error("Error fetching total exams:", error);
    }
  };

  return (
    <div className="row">
      {/* Display dashboard cards */}
      <DashboardCard title="Total Users" count={totalUsers} color="primary" />
      <DashboardCard title="Risk Alerts" count={riskAlerts} color="danger" />
      <DashboardCard title="Total Exams" count={totalExams} color="success" />

      <div className="col-12 mt-4">
        <div className="card">
          <div className="card-body">
            <h2 className="card-title">Welcome, {user?.name}</h2>
          </div>
        </div>
      </div>

      {/* Create Exam Section */}
      <div className="col-12 mt-4">
        <div className="card">
          <div className="card-body">
            <h2 className="card-title">Create Exam</h2>
            <input
              type="text"
              className="form-control"
              placeholder="Enter exam name"
              value={examName}
              onChange={(e) => setExamName(e.target.value)}
            />
            <button className="btn btn-primary mt-2" onClick={handleCreateExam}>
              Create Exam
            </button>
            <div className="mt-4">
              <h2>Created Exams</h2>
              <ul className="list-group">
                {exams.map((exam) => (
                  <li key={exam.id} className="list-group-item">
                    <strong>{exam.name}</strong> (ID: <span className="text-muted">{exam.id}</span>)
                  </li>
                ))}
              </ul>
            </div>

            {/* Additional Features */}
            <div className="mt-4">
              <CreateQuestion /> {/* Manual Question Creation */}
              <GenerateQuestions /> {/* AI-Powered Question Generation */}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

// Dashboard Card Component
const DashboardCard = ({ title, count, color }) => (
  <div className="col-sm-4 mb-4">
    <div className={`card text-white bg-${color}`}>
      <div className="card-body">
        <h5 className="card-title">{title}</h5>
        <p className="card-text display-4">{count}</p>
      </div>
    </div>
  </div>
);

export default DashboardHome;
