import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import { io } from "socket.io-client";

const ExamPage = () => {
  const { examId } = useParams();
  const navigate = useNavigate();

  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const [answers, setAnswers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [timer, setTimer] = useState(3600);
  const [warningMessage, setWarningMessage] = useState(null);
  const [socket, setSocket] = useState(null);
  const [studentId, setStudentId] = useState(null);

  // Retrieve studentId from sessionStorage
  useEffect(() => {
    const id = sessionStorage.getItem("studentId");
    console.log("Retrieved studentId:", id);

    if (!id) {
      alert("You are not logged in. Please log in to continue.");
      navigate("/login");
    } else {
      setStudentId(id);
    }
  }, [navigate]);

  // Initialize WebSocket
  useEffect(() => {
    if (!studentId) return;
    
    const newSocket = io("http://localhost:3001");
    setSocket(newSocket);

    newSocket.on("connect", () => {
      console.log("Connected to WebSocket server:", newSocket.id);
    });

    newSocket.on("risk_warning", (data) => {
      setWarningMessage(data.message);
      setTimeout(() => setWarningMessage(null), 5000);
    });

    newSocket.on("lock_exam", (data) => {
      alert(data.message);
      navigate("/exam-selection");
    });

    return () => {
      newSocket.disconnect();
    };
  }, [navigate, studentId]);

  // Fetch exam questions
  useEffect(() => {
    if (!examId || !studentId) return;

    const fetchQuestions = async () => {
      try {
        const response = await axios.get(`http://localhost:3001/exams/${examId}/questions`, { withCredentials: true });
        setQuestions(response.data.questions);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching questions:", error.response?.data || error.message);
        alert("Failed to load exam questions.");
        setLoading(false);
      }
    };

    fetchQuestions();
  }, [examId, studentId]);

  // Timer countdown
  useEffect(() => {
    const countdown = setInterval(() => {
      setTimer((prev) => {
        if (prev <= 1) {
          clearInterval(countdown);
          handleSubmitExam();
        }
        return prev - 1;
      });
    }, 1000);

    return () => clearInterval(countdown);
  }, []);

  // Centralized event emitter
  const emitEvent = (eventName, data) => {
    if (socket && examId && studentId) {
      socket.emit(eventName, { ...data, examId, studentId, timestamp: Date.now() });
    }
  };

  // Tracking Events (Mouse, Keystroke, Tab Change)
  useEffect(() => {
    if (!studentId) return;

    const handleMouseMove = (e) => emitEvent("mouse_activity", { x: e.clientX, y: e.clientY });
    const handleKeyDown = (e) => emitEvent("keystroke_activity", { key: e.key });
    const handleVisibilityChange = () => {
      if (document.visibilityState === "hidden") emitEvent("tab_change", { action: "Tab switched" });
    };

    window.addEventListener("mousemove", handleMouseMove);
    window.addEventListener("keydown", handleKeyDown);
    document.addEventListener("visibilitychange", handleVisibilityChange);

    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
      window.removeEventListener("keydown", handleKeyDown);
      document.removeEventListener("visibilitychange", handleVisibilityChange);
    };
  }, [socket, examId, studentId]);

  // Submit Exam
  const handleSubmitExam = async () => {
    if (!studentId) return;

    try {
      setLoading(true);
      if (!answers || answers.length !== questions.length) {
        alert("Please answer all questions before submitting.");
        setLoading(false);
        return;
      }

      const payload = { examId, studentId, answers };
      console.log("Submitting exam payload:", payload);

      const response = await axios.post("http://localhost:3001/submit-exam", payload, { withCredentials: true });
      alert(`Exam submitted successfully! Your score: ${response.data.results.score}`);
      navigate("/results", { state: { results: response.data.results } });
    } catch (error) {
      console.error("Error submitting the exam:", error.response?.data || error.message);
      alert("Failed to submit the exam. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <p>Loading...</p>;
  if (!questions.length) return <p>No questions available.</p>;

  return (
    <div className="container mt-5">
      {warningMessage && <div className="alert alert-warning text-center">{warningMessage}</div>}
      <header className="d-flex justify-content-between align-items-center mb-4">
        <h1>Exam</h1>
        <div className="text-danger">Time Left: {Math.floor(timer / 60)}:{("0" + (timer % 60)).slice(-2)}</div>
      </header>
      <div className="card shadow-lg p-4">
        <h4>Q{currentQuestion + 1}: {questions[currentQuestion]?.text}</h4>
        {questions[currentQuestion]?.options?.map((option, index) => (
          <div key={index} className="form-check">
            <input className="form-check-input" type="radio" name="question" id={`option-${index}`} value={option} onChange={() => setSelectedOption(option)} checked={selectedOption === option} />
            <label className="form-check-label" htmlFor={`option-${index}`}>{option}</label>
          </div>
        ))}
        <button className="btn btn-primary mt-4" onClick={() => setCurrentQuestion(currentQuestion + 1)} disabled={!selectedOption}>
          {currentQuestion === questions.length - 1 ? "Finish Exam" : "Next Question"}
        </button>
      </div>
    </div>
  );
};

export default ExamPage;
