import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import io from "socket.io-client";
import axios from "axios";

// Initialize socket.io client
const socket = io("http://localhost:3001");

const ExamPage = () => {
  const { examId } = useParams();
  const navigate = useNavigate();

  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const [alerts, setAlerts] = useState([]);
  const [timer, setTimer] = useState(3600); // Example: 1 hour in seconds
  const [loading, setLoading] = useState(true);

  // Fetch questions for the exam
  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const response = await axios.get(
          `http://localhost:3001/exams/${examId}/questions`,
          { withCredentials: true } // Ensure session cookies are sent
        );
        setQuestions(response.data.questions);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching questions:", error.response?.data || error.message);
        navigate("/exam-selection"); // Redirect if unable to fetch questions
      }
    };

    fetchQuestions();
  }, [examId, navigate]);

  // Timer functionality
  useEffect(() => {
    const countdown = setInterval(() => {
      setTimer((prev) => (prev > 0 ? prev - 1 : 0));
    }, 1000);

    return () => clearInterval(countdown);
  }, []);

  // User activity monitoring with socket.io
  useEffect(() => {
    socket.on("risk_alert", (alert) => {
      setAlerts((prevAlerts) => [...prevAlerts, alert]);
    });

    return () => {
      socket.off("risk_alert");
    };
  }, []);

  return (
    <div className="container mt-5">
      <header className="d-flex justify-content-between align-items-center mb-4">
        <h1 className="text-primary">Exam</h1>
        <div className="text-danger h4">
          Time Left: {Math.floor(timer / 60)}:{("0" + (timer % 60)).slice(-2)}
        </div>
      </header>

      {loading ? (
        <p>Loading questions...</p>
      ) : (
        <div className="card shadow-lg p-4">
          <h4 className="card-title">
            Q{currentQuestion + 1}: {questions[currentQuestion]?.text}
          </h4>
          <div className="mt-3">
            {questions[currentQuestion]?.options?.map((option, index) => (
              <div key={index} className="form-check">
                <input
                  className="form-check-input"
                  type="radio"
                  name="questionOption"
                  id={`option-${index}`}
                  value={option}
                  checked={selectedOption === option}
                  onChange={() => setSelectedOption(option)}
                />
                <label className="form-check-label" htmlFor={`option-${index}`}>
                  {option}
                </label>
              </div>
            ))}
          </div>
        </div>
      )}

      <div className="mt-4">
        <h5 className="text-danger">Alerts</h5>
        {alerts.length > 0 ? (
          alerts.map((alert, index) => (
            <div key={index} className="alert alert-danger">
              {alert.message}
            </div>
          ))
        ) : (
          <p>No alerts</p>
        )}
      </div>
    </div>
  );
};

export default ExamPage;
