import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const ExamSelection = () => {
  const [exams, setExams] = useState([]);
  const navigate = useNavigate();

  const fetchExams = async () => {
    try {
      const response = await axios.get("http://localhost:3001/exams", { withCredentials: true });
      setExams(response.data.exams);
    } catch (error) {
      console.error("Error fetching exams:", error);
    }
  };

  useEffect(() => {
    fetchExams();
  }, []);

  const handleTakeTest = (examId) => {
    // Navigate to the exam page with the exam ID
    navigate(`/start-exam/${examId}`);
  };

  return (
    <div className="container mt-5">
      <h1>Available Exams</h1>
      <ul className="list-group">
        {exams.map((exam) => (
          <li key={exam.id} className="list-group-item d-flex justify-content-between align-items-center">
            {exam.name}
            <button className="btn btn-primary" onClick={() => handleTakeTest(exam.id)}>
              Take Test
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ExamSelection;
