import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const SelectRoles = () => {
  const [role, setRole] = useState("");
  const [googleId, setGoogleId] = useState(""); // State to store Google ID
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch the user profile to get the Google ID
    const fetchUserProfile = async () => {
      try {
        const response = await axios.get("http://localhost:3001/profile", { withCredentials: true });
        setGoogleId(response.data.user.googleId);
      } catch (error) {
        console.error("Error fetching user profile:", error);
      }
    };

    fetchUserProfile();
  }, []);

  const handleRoleSelection = async () => {
    try {
      const response = await axios.post("http://localhost:3001/auth/set-role", {
        googleId,
        role,
      });
      if (response.data.redirectUrl) {
        navigate(response.data.redirectUrl);
      }
    } catch (error) {
      console.error("Role selection failed", error);
    }
  };

  return (
    <div className="container mt-5">
      <h1>Select Your Role</h1>
      <div className="btn-group mt-3" role="group">
        <button className={`btn ${role === "admin" ? "btn-primary" : "btn-outline-primary"}`} onClick={() => setRole("admin")}>
          Admin
        </button>
        <button className={`btn ${role === "student" ? "btn-primary" : "btn-outline-primary"}`} onClick={() => setRole("student")}>
          Student
        </button>
      </div>
      <button className="btn btn-success mt-3" onClick={handleRoleSelection}>
        Submit
      </button>
    </div>
  );
};

export default SelectRoles;
