import React, { useEffect, useState } from "react";

const DynamicWatermark = ({ userName, userID }) => {
  // State to store the position of the watermark
  const [position, setPosition] = useState({ top: "20%", left: "20%" });

  useEffect(() => {
    // Function to randomly change the position of the watermark
    const updatePosition = () => {
      const randomTop = `${Math.random() * 80}%`; // Random position within 80% of the screen height
      const randomLeft = `${Math.random() * 80}%`; // Random position within 80% of the screen width
      setPosition({ top: randomTop, left: randomLeft });
    };

    // Change the position every 5 seconds
    const interval = setInterval(updatePosition, 5000);

    return () => clearInterval(interval); // Cleanup when the component unmounts
  }, []);

  return (
    <div
      style={{
        position: "fixed",
        top: position.top,
        left: position.left,
        opacity: 0.2, // Semi-transparent
        fontSize: "16px",
        color: "black",
        pointerEvents: "none", // Prevent user interaction with the watermark
        transform: "rotate(-30deg)", // Stylized slant
        zIndex: 9999, // Ensures it appears above other elements
        userSelect: "none", // Prevent text selection
      }}
    >
      User: {userName} | ID: {userID} | {new Date().toLocaleTimeString()}
    </div>
  );
};

export default DynamicWatermark;
