import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import io from "socket.io-client";
import axios from "axios";

const socket = io("http://localhost:3001");

const ExamPage = ({ studentName, studentEmail, testName }) => {
  const { examId } = useParams();
  const navigate = useNavigate();
  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const [alerts, setAlerts] = useState([]);
  const [timer, setTimer] = useState(3600); // Example: 1 hour in seconds
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Start Exam
    const startExam = async () => {
      try {
        await axios.post("http://localhost:3001/start-exam", {
          studentName,
          studentEmail,
          testName,
          examId,
        });
      } catch (error) {
        console.error("Error starting exam:", error);
      }
    };

    startExam();

    // Risk alerts via socket
    socket.on("risk_alert", (alert) => {
      setAlerts((prevAlerts) => [...prevAlerts, alert]);
    });

    return () => {
      socket.off("risk_alert");
    };
  }, [studentName, studentEmail, testName, examId]);

  useEffect(() => {
    // Fetch questions
    const fetchQuestions = async () => {
      try {
        const response = await axios.get(
          `http://localhost:3001/exams/${examId}/questions`,
          { withCredentials: true }
        );
        setQuestions(response.data.questions);
        setLoading(false); // Questions loaded
      } catch (error) {
        console.error("Error fetching questions:", error);
        navigate("/exam-selection");
      }
    };

    fetchQuestions();
  }, [examId, navigate]);

  useEffect(() => {
    // Timer
    const countdown = setInterval(() => {
      setTimer((prev) => (prev > 0 ? prev - 1 : 0));
    }, 1000);

    return () => clearInterval(countdown);
  }, []);

  const handleNextQuestion = () => {
    setCurrentQuestion((prev) =>
      prev < questions.length - 1 ? prev + 1 : prev
    );
    setSelectedOption(null); // Reset selected option for the next question
  };

  const handleOptionChange = (option) => {
    setSelectedOption(option);
  };

  return (
    <div className="container mt-5">
      {/* Timer and Header */}
      <header className="d-flex justify-content-between align-items-center mb-4">
        <h1 className="text-primary">Exam: {testName}</h1>
        <div className="text-danger h4">
          Time Left: {Math.floor(timer / 60)}:{("0" + (timer % 60)).slice(-2)}
        </div>
      </header>

      {loading ? (
        <p>Loading questions...</p>
      ) : (
        <div className="card shadow-lg p-4">
          {/* Current Question */}
          <h4 className="card-title">Q{currentQuestion + 1}: {questions[currentQuestion]?.text}</h4>
          <div className="mt-3">
            {/* Options */}
            {questions[currentQuestion]?.options?.map((option, index) => (
              <div key={index} className="form-check">
                <input
                  className="form-check-input"
                  type="radio"
                  name="questionOption"
                  id={`option-${index}`}
                  value={option}
                  checked={selectedOption === option}
                  onChange={() => handleOptionChange(option)}
                />
                <label className="form-check-label" htmlFor={`option-${index}`}>
                  {option}
                </label>
              </div>
            ))}
          </div>

          {/* Navigation Buttons */}
          <div className="mt-4 d-flex justify-content-between">
            <button
              className="btn btn-secondary"
              disabled={currentQuestion === 0}
              onClick={() => setCurrentQuestion((prev) => prev - 1)}
            >
              Previous
            </button>
            <button
              className="btn btn-primary"
              onClick={handleNextQuestion}
              disabled={currentQuestion >= questions.length - 1}
            >
              Next
            </button>
          </div>
        </div>
      )}

      {/* Risk Alerts */}
      <div className="mt-4">
        <h5 className="text-danger">Alerts</h5>
        {alerts.length > 0 ? (
          alerts.map((alert, index) => (
            <p key={index} className="alert alert-danger">
              {alert.message}
            </p>
          ))
        ) : (
          <p>No alerts</p>
        )}
      </div>
    </div>
  );
};

export default ExamPage;
