import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";

const ResultsPage = () => {
  const location = useLocation();
  const { examId } = location.state || {};
  const [results, setResults] = useState(null);

  useEffect(() => {
    const fetchResults = async () => {
      try {
        const response = await axios.get(`http://localhost:3001/exam-results?examId=${examId}`, {
          withCredentials: true,
        });
        setResults(response.data.results);
      } catch (error) {
        console.error("Error fetching results:", error.response?.data || error.message);
      }
    };

    if (examId) {
      fetchResults();
    }
  }, [examId]);

  if (!results) {
    return <p>Loading results...</p>;
  }

  return (
    <div className="container mt-5">
      <h1 className="text-success">Exam Results</h1>
      <p><strong>Score:</strong> {results.score}/{results.totalQuestions}</p>
      <p><strong>Exam ID:</strong> {examId}</p>
      <button onClick={() => window.location.href = "/exam-selection"} className="btn btn-primary">
        Go Back to Exams
      </button>
    </div>
  );
};

export default ResultsPage;
