import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const ExamPage = () => {
  const navigate = useNavigate();
  const [exams, setExams] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedExamId, setSelectedExamId] = useState(null);

  const [studentName, setStudentName] = useState("");
  const [studentEmail, setStudentEmail] = useState("");

  useEffect(() => {
    // Fetch the available exams
    const fetchExams = async () => {
      try {
        const response = await axios.get("http://localhost:3001/exams", { withCredentials: true });
        setExams(response.data.exams);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching exams:", error.response?.data || error.message);
      }
    };

    fetchExams();
  }, []);

  const handleStartExam = (examId, testName) => {
    if (!studentName || !studentEmail) {
      alert("Please fill in your details before starting the exam!");
      return;
    }

    // Save user details and test name in local storage
    localStorage.setItem("studentName", studentName);
    localStorage.setItem("studentEmail", studentEmail);
    localStorage.setItem("testName", testName);

    // Navigate to the ExamPage with selected exam ID
    navigate(`/start-exam/${examId}`);
  };

  return (
    <div className="container mt-5">
      <h1 className="mb-4 text-primary">Select an Exam</h1>

      {/* Student Details */}
      <div className="card mb-4 p-3 shadow">
        <h5>Enter Your Details</h5>
        <div className="form-group mb-3">
          <label htmlFor="studentName">Name:</label>
          <input
            type="text"
            className="form-control"
            id="studentName"
            value={studentName}
            onChange={(e) => setStudentName(e.target.value)}
            required
          />
        </div>
        <div className="form-group mb-3">
          <label htmlFor="studentEmail">Email:</label>
          <input
            type="email"
            className="form-control"
            id="studentEmail"
            value={studentEmail}
            onChange={(e) => setStudentEmail(e.target.value)}
            required
          />
        </div>
      </div>

      {loading ? (
        <p>Loading exams...</p>
      ) : exams.length === 0 ? (
        <p>No exams available at the moment.</p>
      ) : (
        <div className="list-group">
          {exams.map((exam) => (
            <div className="list-group-item list-group-item-action" key={exam.id}>
              <h5>{exam.name}</h5>
              <button
                className="btn btn-primary mt-2"
                onClick={() => handleStartExam(exam.id, exam.name)}
              >
                Start Exam
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ExamPage;
