// src/ExamPage.js

import React, { useState, useEffect } from "react";
import io from "socket.io-client";

const socket = io("http://localhost:3001");

const ExamPage = () => {
  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [alerts, setAlerts] = useState([]);
  const [timer, setTimer] = useState(3600); // 1 hour example

  useEffect(() => {
    socket.on("risk_alert", (alert) => {
      setAlerts((prevAlerts) => [...prevAlerts, alert]);
    });

    return () => {
      socket.off("risk_alert");
    };
  }, []);

  useEffect(() => {
    fetch("/api/questions")
      .then((response) => response.json())
      .then((data) => setQuestions(data));
  }, []);

  useEffect(() => {
    const countdown = setInterval(() => {
      setTimer((prev) => (prev > 0 ? prev - 1 : 0));
    }, 1000);

    return () => clearInterval(countdown);
  }, []);

  const handleNextQuestion = () => {
    setCurrentQuestion((prev) => (prev < questions.length - 1 ? prev + 1 : prev));
  };

  useEffect(() => {
    const handleMouseMove = (event) => {
      socket.emit("mouse_movement", {
        x: event.clientX,
        y: event.clientY,
        timestamp: Date.now(),
      });
    };

    const handleKeyPress = (event) => {
      socket.emit("keystroke_pattern", {
        key: event.key,
        timestamp: Date.now(),
      });
    };

    window.addEventListener("mousemove", handleMouseMove);
    window.addEventListener("keydown", handleKeyPress);

    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
      window.removeEventListener("keydown", handleKeyPress);
    };
  }, []);

  return (
    <div className="p-4">
      <header className="flex justify-between items-center">
        <h1>Exam Title</h1>
        <span>{`Time Left: ${Math.floor(timer / 60)}:${timer % 60}`}</span>
      </header>
      <div className="mt-4">
        <h2>{questions[currentQuestion]?.questionText}</h2>
        {/* Add options and input elements here */}
      </div>
      <div className="mt-4">
        <button onClick={handleNextQuestion}>Next</button>
      </div>
      <div className="mt-4 text-red-600">
        {alerts.map((alert, index) => (
          <p key={index}>{alert.message}</p>
        ))}
      </div>
    </div>
  );
};

export default ExamPage;
