import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const HomePage = () => {
  // Dynamic feature data
  const features = [
    {
      title: "Behavioral Monitoring",
      description:
        "Detect suspicious activities through mouse movements, keystrokes, and window shifts.",
      icon: "https://via.placeholder.com/100", // Replace with actual icon URLs
    },
    {
      title: "Dynamic Risk Scoring",
      description:
        "Real-time analytics calculate and adjust risk scores based on user behavior.",
      icon: "https://via.placeholder.com/100", // Replace with actual icon URLs
    },
    {
      title: "Privacy Compliance",
      description:
        "GDPR and FERPA compliant—ensures no video or audio invasion during assessments.",
      icon: "https://via.placeholder.com/100", // Replace with actual icon URLs
    },
    {
      title: "Automated Alerts",
      description:
        "Proactive notifications ensure timely interventions during flagged activities.",
      icon: "https://via.placeholder.com/100", // Replace with actual icon URLs
    },
  ];

  return (
    <div>
      {/* Hero Section */}
      <section
        className="hero d-flex align-items-center text-white text-center"
        style={{
          backgroundImage: `url('https://via.placeholder.com/1920x1080')`,
          backgroundSize: "cover",
          backgroundPosition: "center",
          minHeight: "100vh",
        }}
      >
        <div className="container">
          <h1 className="display-4 fw-bold">Welcome to Your Dynamic Platform</h1>
          <p className="lead">
            Empowering your journey with advanced solutions, stunning design, and
            unparalleled experiences.
          </p>
          <a href="#features" className="btn btn-primary btn-lg mt-3">
            Explore Features
          </a>
        </div>
      </section>

      {/* Features Section */}
      <section id="features" className="features py-5 bg-light">
        <div className="container">
          <h2 className="text-center mb-5">Key Features</h2>
          <div className="row g-4">
            {features.map((feature, index) => (
              <div key={index} className="col-md-6 col-lg-4">
                <div className="card p-4 text-center border-0 shadow-sm">
                  <img
                    src={feature.icon}
                    alt={`${feature.title} Icon`}
                    className="mx-auto mb-3"
                    width="70"
                  />
                  <h5>{feature.title}</h5>
                  <p>{feature.description}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Footer */}
      <footer className="footer bg-dark text-white text-center py-3">
        <p>
          &copy; 2025 Dynamic Platform. All rights reserved.{" "}
          <a href="/privacy-policy" className="text-warning">
            Privacy Policy
          </a>
        </p>
      </footer>
    </div>
  );
};

export default HomePage;
