import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import { io } from "socket.io-client";

const ExamPage = () => {
  const { examId } = useParams();
  const navigate = useNavigate();
  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const [answers, setAnswers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [timer, setTimer] = useState(3600); // 1-hour timer
  const [warningMessage, setWarningMessage] = useState(null);
  const [socket, setSocket] = useState(null);

  // Real-time metrics
  const [metrics, setMetrics] = useState({
    mouse_speed: 0,
    key_press_intervals: 0,
    tab_switch_frequency: 0,
  });
  const [lastKeyPressTime, setLastKeyPressTime] = useState(Date.now());

  // Initialize WebSocket connection
  useEffect(() => {
    const newSocket = io("http://localhost:3001");
    setSocket(newSocket);

    newSocket.on("connect", () => {
      console.log("Connected to WebSocket server:", newSocket.id);
    });

    newSocket.on("risk_warning", (data) => {
      console.log("Risk warning received from WebSocket:", data);
      setWarningMessage(data.message);
      setTimeout(() => setWarningMessage(null), 5000);
    });

    newSocket.on("lock_exam", (data) => {
      alert(data.message);
      navigate("/exam-selection");
    });

    return () => {
      newSocket.disconnect();
    };
  }, [navigate]);

  // Fetch exam questions
  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const response = await axios.get(
          `http://localhost:3001/exams/${examId}/questions`,
          { withCredentials: true }
        );
        setQuestions(response.data.questions);
      } catch (error) {
        console.error("Error fetching questions:", error.response?.data || error.message);
        alert("Failed to load exam questions.");
      } finally {
        setLoading(false);
      }
    };

    fetchQuestions();
  }, [examId]);

  // Timer countdown logic
  useEffect(() => {
    const countdown = setInterval(() => {
      setTimer((prev) => {
        if (prev <= 1) {
          clearInterval(countdown);
          handleSubmitExam(); // Auto-submit when the timer ends
        }
        return prev - 1;
      });
    }, 1000);

    return () => clearInterval(countdown); // Cleanup on unmount
  }, []);

  // Emit mouse movement
  useEffect(() => {
    const handleMouseMove = (e) => {
      const mouseSpeed = Math.sqrt(e.movementX ** 2 + e.movementY ** 2);
      setMetrics((prev) => ({
        ...prev,
        mouse_speed: (prev.mouse_speed + mouseSpeed) / 2, // Average speed
      }));
    };
    window.addEventListener("mousemove", handleMouseMove);

    return () => window.removeEventListener("mousemove", handleMouseMove);
  }, []);

  // Emit keystroke activity
  useEffect(() => {
    const handleKeyDown = () => {
      const currentTime = Date.now();
      const interval = currentTime - lastKeyPressTime;
      setLastKeyPressTime(currentTime);

      setMetrics((prev) => ({
        ...prev,
        key_press_intervals: (prev.key_press_intervals + interval) / 2, // Average interval
      }));
    };

    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  }, [lastKeyPressTime]);

  // Emit tab change activity
  useEffect(() => {
    const handleVisibilityChange = () => {
      if (document.visibilityState === "hidden") {
        setMetrics((prev) => ({
          ...prev,
          tab_switch_frequency: prev.tab_switch_frequency + 1, // Increment tab switches
        }));
      }
    };
    document.addEventListener("visibilitychange", handleVisibilityChange);

    return () => document.removeEventListener("visibilitychange", handleVisibilityChange);
  }, []);

  // Send metrics to Flask API for prediction
  useEffect(() => {
    

    const sendMetricsForPrediction = async () => {
      try {
        const response = await axios.post("http://127.0.0.1:5000/predict", metrics, {
          headers: { "Content-Type": "application/json" },
        });

        const riskLevel = response.data.risk_level;
        console.log("Predicted Risk Level Response:", response.data);
        console.log("Predicted Risk Level:", riskLevel);
        console.log("Sending metrics for prediction...");

        // Trigger warnings or alerts based on risk level
        if (riskLevel === "high") {
          setWarningMessage("High-risk behavior detected. Stay focused!");
        } else if (riskLevel === "medium") {
          setWarningMessage("Medium-risk behavior detected. Please focus.");
        }
      } catch (error) {
        console.error("Error sending metrics for prediction:", error.response?.data || error.message);
      }
    };
    console.log("Updated metrics:", metrics);
    // Periodically send metrics every 5 seconds
    const interval = setInterval(() => {
      sendMetricsForPrediction();
    }, 5000);

    return () => clearInterval(interval);
    console.log("Sending to /predict:", metrics);
// Cleanup interval on component unmount
  }, [metrics]);

  const handleOptionChange = (option) => {
    setSelectedOption(option);
    setAnswers((prev) => {
      const updatedAnswers = [...prev];
      updatedAnswers[currentQuestion] = {
        questionId: questions[currentQuestion]?.id,
        answer: option,
      };
      return updatedAnswers;
    });
  };

  const handleNextQuestion = () => {
    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(currentQuestion + 1); // Move to the next question
      setSelectedOption(null); // Reset the selected option for the next question
    } else {
      handleSubmitExam(); // Automatically submit when on the last question
    }
  };

  const handleSubmitExam = async () => {
    try {
      setLoading(true);

      if (answers.length !== questions.length) {
        alert("Please answer all questions before submitting.");
        setLoading(false);
        return;
      }

      const payload = {
        examId,
        studentId: 1, // Replace with actual studentId from authentication logic
        answers,
      };

      const response = await axios.post("http://localhost:3001/submit-exam", payload, {
        withCredentials: true,
      });

      const results = response.data.results;
      alert(`Exam submitted successfully! Your score: ${results.score}/${results.totalQuestions}`);
      navigate("/results", { state: { results } });
    } catch (error) {
      console.error("Error submitting the exam:", error.response?.data || error.message);
      alert("An unexpected error occurred. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <p>Loading questions...</p>;
  }

  if (questions.length === 0) {
    return <p>No questions available for this exam.</p>;
  }

  const currentQ = questions[currentQuestion];

  return (
    <div className="container mt-5">
      {/* Warning banner for risk activity */}
      {warningMessage && (
        <div className="alert alert-warning text-center" role="alert">
          {warningMessage}
        </div>
      )}

      {/* Header Section */}
      <header className="d-flex justify-content-between align-items-center mb-4">
        <h1 className="text-primary">Exam</h1>
        <div className="text-danger h4">
          Time Left: {Math.floor(timer / 60)}:{("0" + (timer % 60)).slice(-2)}
        </div>
      </header>

      {/* Current Question Section */}
      {currentQ ? (
        <div className="card shadow-lg p-4">
          <h4 className="card-title">
            Q{currentQuestion + 1}: {currentQ.text}
          </h4>
          <div className="mt-3">
            {/* Render Options Dynamically */}
            {currentQ.options?.map((option, index) => (
              <div key={index} className="form-check">
                <input
                  className="form-check-input"
                  type="radio"
                  name={`question-${currentQuestion}`}
                  id={`option-${index}`}
                  value={option.trim()}
                  onChange={() => handleOptionChange(option.trim())}
                  checked={selectedOption === option.trim()}
                  />
                  <label className="form-check-label" htmlFor={`option-${index}`}>
                    {option}
                  </label>
                </div>
              ))}
            </div>
  
            {/* Navigation Button */}
            <button
              className="btn btn-primary mt-4"
              onClick={handleNextQuestion}
              disabled={selectedOption === null} // Disable button until an option is selected
            >
              {currentQuestion === questions.length - 1 ? "Finish Exam" : "Next Question"}
            </button>
          </div>
        ) : (
          <p>No more questions available.</p>
        )}
      </div>
    );
  };
  
  export default ExamPage;
