import React, { useEffect, useState } from "react";
import axios from "axios";
import CreateQuestion from "./CreateQuestion";
import GenerateQuestions from "./GenerateQuestions";
import { io } from "socket.io-client";
import DynamicWatermark from "./DynamicWatermark";

const DashboardHome = ({ user, handleCreateExam, examName, setExamName, exams }) => {
  const [totalUsers, setTotalUsers] = useState(0);
  const [riskAlerts, setRiskAlerts] = useState(0);
  const [totalExams, setTotalExams] = useState(0);

  useEffect(() => {
    // WebSocket setup for real-time updates
    const socket = io("http://localhost:3001");

    socket.on("dashboard_update", (data) => {
      if (data.totalUsers) setTotalUsers(data.totalUsers);
      if (data.riskAlerts) setRiskAlerts(data.riskAlerts);
      if (data.totalExams) setTotalExams(data.totalExams);
    });

    return () => socket.disconnect(); // Cleanup on component unmount
  }, []);

  useEffect(() => {
    // Fetch initial dashboard data
    const fetchData = async () => {
      try {
        const [userResponse, alertsResponse, examsResponse] = await Promise.all([
          axios.get("http://localhost:3001/total-users", { withCredentials: true }),
          axios.get("http://localhost:3001/risk-alerts", { withCredentials: true }),
          axios.get("http://localhost:3001/total-exams", { withCredentials: true }),
        ]);
        setTotalUsers(userResponse.data.totalUsers);
        setRiskAlerts(alertsResponse.data.riskAlerts);
        setTotalExams(examsResponse.data.totalExams);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="row">
      {/* Dashboard Metrics */}
      {[
        { title: "Total Users", count: totalUsers, color: "primary" },
        { title: "Risk Alerts", count: riskAlerts, color: "danger" },
        { title: "Total Exams", count: totalExams, color: "success" },
      ].map((metric, index) => (
        <DashboardCard key={index} title={metric.title} count={metric.count} color={metric.color} />
      ))}

      {/* Welcome Section */}
      <div className="col-12 mt-4">
        <div className="card">
          <div className="card-body">
            <h2 className="card-title">Welcome, {user?.name || "User"}!</h2>
          </div>
        </div>
      </div>

      {/* Exam Creation Section */}
      <div className="col-12 mt-4">
        <div className="card">
          <div className="card-body">
            <h2 className="card-title">Create Exam</h2>
            <input
              type="text"
              className="form-control"
              placeholder="Enter exam name"
              value={examName}
              onChange={(e) => setExamName(e.target.value)}
            />
            <button className="btn btn-primary mt-2" onClick={handleCreateExam}>
              Create Exam
            </button>

            <div className="mt-4">
              <h2>Created Exams</h2>
              <ul className="list-group">
                {exams.map((exam) => (
                  <li key={exam.id} className="list-group-item">
                    <strong>{exam.name}</strong> (ID: <span className="text-muted">{exam.id}</span>)
                  </li>
                ))}
              </ul>
            </div>

            {/* Question Creation Section */}
            <div className="mt-4">
              <CreateQuestion />
              <GenerateQuestions />
            </div>
          </div>
        </div>
      </div>

      {/* Exam Screen */}
      <div className="col-12 mt-4">
        <div className="card">
          <div className="card-body">
            <h2 className="card-title">Exam Screen</h2>
            <p>Your activity is being monitored.</p>
            <DynamicWatermark
              fetchUserDetails={async () => {
                const response = await axios.get("http://localhost:3001/api/user-details");
                return response.data;
              }}
            />
            <div className="question">
              <h3>Question 1:</h3>
              <p>What is the capital of France?</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

const DashboardCard = ({ title, count, color }) => (
  <div className="col-sm-4 mb-4">
    <div className={`card text-white bg-${color}`}>
      <div className="card-body">
        <h5 className="card-title">{title}</h5>
        <p className="card-text display-4">{count}</p>
      </div>
    </div>
  </div>
);

export default DashboardHome;
