import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import { io } from "socket.io-client";

const ExamPage = () => {
  const { examId } = useParams();
  const navigate = useNavigate();
  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const [answers, setAnswers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [timer, setTimer] = useState(3600); // 1-hour timer
  const [warningMessage, setWarningMessage] = useState(null);
  const [socket, setSocket] = useState(null);

  // Initialize WebSocket connection
  useEffect(() => {
    const newSocket = io("http://localhost:3001");
    setSocket(newSocket);

    newSocket.on("connect", () => {
      console.log("Connected to WebSocket server:", newSocket.id);
    });

    // Listen for risk warnings and display them
    newSocket.on("risk_warning", (data) => {
      setWarningMessage(data.message);
      setTimeout(() => setWarningMessage(null), 5000);
    });

    // Listen for exam lock event and redirect to /exam-selection
    newSocket.on("lock_exam", (data) => {
      alert(data.message); // Notify user about the lock
      navigate("/exam-selection"); // Redirect to exam selection
    });

    return () => {
      newSocket.disconnect(); // Cleanup on unmount
    };
  }, [navigate]);

  // Fetch exam questions
  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const response = await axios.get(
          `http://localhost:3001/exams/${examId}/questions`,
          { withCredentials: true }
        );
        setQuestions(response.data.questions);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching questions:", error.response?.data || error.message);
        alert("Failed to load exam questions.");
        setLoading(false);
      }
    };

    fetchQuestions();
  }, [examId]);

  // Timer countdown logic
  useEffect(() => {
    const countdown = setInterval(() => {
      setTimer((prev) => {
        if (prev <= 1) {
          clearInterval(countdown);
          handleSubmitExam(); // Auto-submit when the timer ends
        }
        return prev - 1;
      });
    }, 1000);

    return () => clearInterval(countdown); // Cleanup on unmount
  }, []);

  // Centralized emit function for WebSocket events
  const studentId = 1; // Replace this with the actual studentId (e.g., from authentication context)

// Centralized emit function for WebSocket events
const emitEvent = (eventName, data) => {
  if (socket && examId && studentId) {
    socket.emit(eventName, { ...data, examId, studentId, timestamp: Date.now() });
  } else {
    console.error(`Failed to emit ${eventName}: socket, examId, or studentId is undefined.`);
  }
};

// Emit mouse movement
useEffect(() => {
  const handleMouseMove = (e) => {
    emitEvent("mouse_activity", { x: e.clientX, y: e.clientY });
  };
  window.addEventListener("mousemove", handleMouseMove);

  return () => window.removeEventListener("mousemove", handleMouseMove);
}, [socket, examId, studentId]);

// Emit keystroke activity
useEffect(() => {
  const handleKeyDown = (e) => {
    if (document.visibilityState === "visible") {
      emitEvent("keystroke_activity", { key: e.key }); // Reduce risk only if focused
    } else {
      console.warn("Keystroke detected outside exam tab");
    }
  };
  
  window.addEventListener("keydown", handleKeyDown);

  return () => window.removeEventListener("keydown", handleKeyDown);
}, [socket, examId, studentId]);

// Emit tab change activity
useEffect(() => {
  const handleVisibilityChange = () => {
    if (document.visibilityState === "hidden") {
      emitEvent("tab_change", { action: "Tab switched" });
    }
  };
  document.addEventListener("visibilitychange", handleVisibilityChange);

  return () => document.removeEventListener("visibilitychange", handleVisibilityChange);
}, [socket, examId, studentId]);


  // Tab change tracking
  useEffect(() => {
    const handleVisibilityChange = () => {
      if (document.visibilityState === "hidden") {
        emitEvent("tab_change", { action: "Tab switched" });
      }
    };
    document.addEventListener("visibilitychange", handleVisibilityChange);

    return () => document.removeEventListener("visibilitychange", handleVisibilityChange);
  }, [socket, examId]);

  // Handle answer selection
  const handleOptionChange = (option) => {
    setSelectedOption(option);

    // Save the selected answer for the current question
    setAnswers((prev) => {
      const updatedAnswers = [...prev];
      updatedAnswers[currentQuestion] = {
        questionId: questions[currentQuestion].id,
        answer: option,
      };
      return updatedAnswers;
    });
  };

  // Handle navigation to the next question or submit the exam
  const handleNextQuestion = () => {
    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
      setSelectedOption(null); // Reset selected option
    } else {
      handleSubmitExam(); // Submit when on the last question
    }
  };

  // Submit the exam to the backend
  const handleSubmitExam = async () => {
    try {
      // Disable the submit button and show a loading indicator
      setLoading(true);
  
      const response = await axios.post(
        "http://localhost:3001/submit-exam",
        {
          examId,
          answers,
        },
        { withCredentials: true }
      );
  
      const results = response.data.results;
      alert(`Exam submitted successfully! Your score: ${results.score}/${results.totalQuestions}`);
      
      // Redirect to results page
      navigate("/results", { state: { results } });
    } catch (error) {
      console.error("Error submitting the exam:", error.response?.data || error.message);
  
      // Provide more detailed user feedback
      if (error.response) {
        alert(`Failed to submit the exam: ${error.response.data.error || "Unknown error occurred."}`);
      } else {
        alert("An unexpected error occurred. Please try again.");
      }
    } finally {
      // Re-enable the submit button and hide the loading indicator
      setLoading(false);
    }
  };
  
  if (loading) {
    return <p>Loading questions...</p>;
  }

  if (questions.length === 0) {
    return <p>No questions available for this exam.</p>;
  }

  const currentQ = questions[currentQuestion]; // Get current question safely

  return (
    <div className="container mt-5">
      {/* Warning banner for risk activity */}
      {warningMessage && (
        <div className="alert alert-warning text-center" role="alert">
          {warningMessage}
        </div>
      )}

      <header className="d-flex justify-content-between align-items-center mb-4">
        <h1 className="text-primary">Exam</h1>
        <div className="text-danger h4">
          Time Left: {Math.floor(timer / 60)}:{("0" + (timer % 60)).slice(-2)}
        </div>
      </header>

      {currentQ ? (
        <div className="card shadow-lg p-4">
          <h4 className="card-title">
            Q{currentQuestion + 1}: {currentQ.text}
          </h4>
          <div className="mt-3">
            {currentQ.options?.map((option, index) => (
              <div key={index} className="form-check">
                <input
                  className="form-check-input"
                  type="radio"
                  name={`question-${currentQuestion}`}
                  id={`option-${index}`}
                  value={option}
                  onChange={() => handleOptionChange(option)}
                  checked={selectedOption === option}
                />
                <label className="form-check-label" htmlFor={`option-${index}`}>
                  {option}
                </label>
              </div>
            ))}
          </div>
          <button
            className="btn btn-primary mt-4"
            onClick={handleNextQuestion}
            disabled={selectedOption === null}
          >
            {currentQuestion === questions.length - 1 ? "Finish Exam" : "Next Question"}
          </button>
        </div>
      ) : (
        <p>No more questions available.</p>
      )}
    </div>
  );
};

export default ExamPage;
