import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const ExamSelection = () => {
  const [exams, setExams] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchExams = async () => {
      try {
        const response = await axios.get("http://localhost:3001/exams", {
          withCredentials: true,
        });
        setExams(response.data.exams);
      } catch (error) {
        console.error("Error fetching exams:", error.response?.data || error.message);
      }
    };

    fetchExams();
  }, []);

  const handleTakeExam = (examId) => {
    navigate(`/start-exam/${examId}`);
  };

  const handleViewResults = (examId) => {
    navigate(`/results`, { state: { examId } });
  };

  return (
    <div className="container mt-5">
      <h1 className="text-primary">Available Exams</h1>
      {exams.map((exam) => (
        <div key={exam.id} className="card mb-3 p-3 shadow-sm">
          <h5>{exam.name}</h5>
          <p>{exam.description}</p>
          {exam.attempted ? (
            <button
              className="btn btn-success"
              onClick={() => handleViewResults(exam.id)}
            >
              View Results
            </button>
          ) : (
            <button
              className="btn btn-primary"
              onClick={() => handleTakeExam(exam.id)}
            >
              Take Exam
            </button>
          )}
        </div>
      ))}
    </div>
  );
};

export default ExamSelection;
