import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap-icons/font/bootstrap-icons.css";

function SignIn() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState("");
  const [theme, setTheme] = useState(localStorage.getItem("theme") || "light");
  const navigate = useNavigate();

  useEffect(() => {
    document.documentElement.setAttribute("data-bs-theme", theme);
    localStorage.setItem("theme", theme);
  }, [theme]);

  const handleThemeChange = (selectedTheme) => {
    setTheme(selectedTheme);
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setError("");
    try {
      const response = await fetch("http://localhost:3001/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ email, password }),
      });
      const data = await response.json();
      if (!response.ok) throw new Error(data.error);
      
      // Redirect based on user role
      if (data.role === "admin") {
        navigate("/dashboard");
      } else {
        navigate("/exam");
      }
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className="container vh-100 d-flex justify-content-center align-items-center">
      <div className="card p-4 shadow-lg" style={{ width: "100%", maxWidth: "400px" }}>
        <form className="text-center" onSubmit={handleLogin}>
          <h1 className="h3 mb-3 fw-normal">Please sign in</h1>
          <div className="form-floating mb-2">
            <input type="email" className="form-control" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
            <label>Email address</label>
          </div>
          <div className="form-floating mb-2 position-relative">
            <input type={showPassword ? "text" : "password"} className="form-control" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
            <label>Password</label>
            <button type="button" className="btn position-absolute end-0 top-50 translate-middle-y me-2" onClick={() => setShowPassword(!showPassword)} style={{ border: "none", background: "transparent" }}>
              <i className={`bi ${showPassword ? "bi-eye-slash" : "bi-eye"}`} style={{ fontSize: "1.2rem" }}></i>
            </button>
          </div>
          {error && <p className="text-danger">{error}</p>}
          <button className="btn btn-primary w-100 py-2 mt-2" type="submit">Sign in</button>
        </form>
        <div className="text-center mt-3">
          <a href="http://localhost:3001/auth/google" className="btn btn-danger mt-2">
            <i className="bi bi-google"></i> Sign in with Google
          </a>
        </div>
      </div>
    </div>
  );
}

export default SignIn;
