import React, { useEffect, useState } from "react";

const DynamicWatermark = ({ fetchUserDetails }) => {
  const [userData, setUserData] = useState(null);

  // Fetch user details dynamically
  useEffect(() => {
    const getUserData = async () => {
      const data = await fetchUserDetails(); // Call the function passed via props
      setUserData(data); // Set the user data dynamically
    };
    getUserData();
  }, [fetchUserDetails]);

  return (
    userData && (
      <div
        style={{
          position: "absolute",
          top: "50%", // Center vertically
          left: "50%", // Center horizontally
          transform: "translate(-50%, -50%)", // Center precisely
          fontSize: "24px",
          opacity: 0.2, // Semi-transparent
          fontWeight: "bold",
          color: "black",
          pointerEvents: "none", // Prevent user interaction
          zIndex: 9999, // Ensure it stays above everything
          userSelect: "none", // Prevent text selection
        }}
      >
        User: {userData.name} | ID: {userData.id} | {new Date().toLocaleTimeString()}
      </div>
    )
  );
};

export default DynamicWatermark;
