import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import io from "socket.io-client";
import axios from "axios";

// Initialize socket.io client
const socket = io("http://localhost:3001");

const ExamPage = ({ studentName, studentEmail, testName }) => {
  const { examId } = useParams();
  const navigate = useNavigate();
  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const [alerts, setAlerts] = useState([]);
  const [timer, setTimer] = useState(3600); // 1 hour in seconds
  const [loading, setLoading] = useState(true);
  const [isExamCompleted, setIsExamCompleted] = useState(false);

  useEffect(() => {
    console.log("ExamPage props:", { studentName, studentEmail, testName, examId });

    if (!examId) {
      console.error("Exam ID is missing.");
      navigate("/exam-selection");
    }
  }, [studentName, studentEmail, testName, examId, navigate]);

  // Start Exam
  useEffect(() => {
    const startExam = async () => {
      if (!studentName || !studentEmail || !testName || !examId) {
        console.error("Missing required fields for starting the exam.");
        alert("Error: Missing required fields.");
        navigate("/exam-selection");
        return;
      }

      try {
        console.log("Starting exam with:", { studentName, studentEmail, testName, examId });

        await axios.post(
          "http://localhost:3001/start-exam",
          { studentName, studentEmail, testName, examId },
          { withCredentials: true }
        );

        console.log("Exam started successfully");
      } catch (error) {
        console.error("Error starting exam:", error.response?.data || error.message);
        if (error.response?.status === 401) navigate("/login");
        if (error.response?.status === 400) alert("Error: Missing required fields.");
      }
    };

    startExam();

    socket.on("risk_alert", (alert) => {
      setAlerts((prevAlerts) => [...prevAlerts, alert]);
    });

    return () => socket.off("risk_alert");
  }, [studentName, studentEmail, testName, examId, navigate]);

  // Fetch Questions
  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        console.log("Fetching questions for examId:", examId);

        const response = await axios.get(`http://localhost:3001/get-exam-questions/${examId}`, {
          withCredentials: true,
        });

        if (!response.data.questions || response.data.questions.length === 0) {
          throw new Error("No questions found for this exam.");
        }

        setQuestions(response.data.questions);
        setLoading(false);
        console.log("Questions fetched successfully:", response.data.questions);
      } catch (error) {
        console.error("Error fetching questions:", error.response?.data || error.message);
        alert("Error fetching questions. Please try again.");
        navigate("/exam-selection");
      }
    };

    fetchQuestions();
  }, [examId, navigate]);

  // Timer
  useEffect(() => {
    const countdown = setInterval(() => {
      setTimer((prev) => (prev > 0 ? prev - 1 : 0));
    }, 1000);

    return () => clearInterval(countdown);
  }, []);

  // Navigation Handlers
  const handleNextQuestion = () => {
    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion((prev) => prev + 1);
      setSelectedOption(null);
    } else {
      setIsExamCompleted(true);
    }
  };

  const handlePreviousQuestion = () => {
    setCurrentQuestion((prev) => (prev > 0 ? prev - 1 : 0));
  };

  const handleOptionChange = (option) => {
    setSelectedOption(option);
  };

  return (
    <div className="container mt-5">
      <header className="d-flex justify-content-between align-items-center mb-4">
        <h1 className="text-primary">Exam: {testName}</h1>
        <div className="text-danger h4">
          Time Left: {Math.floor(timer / 60)}:{("0" + (timer % 60)).slice(-2)}
        </div>
      </header>

      {loading ? (
        <p>Loading questions...</p>
      ) : isExamCompleted ? (
        <div className="card shadow-lg p-4 text-center">
          <h2 className="text-success">Congratulations!</h2>
          <p>You have completed the exam.</p>
          <button className="btn btn-primary" onClick={() => navigate("/exam-selection")}>
            Go Back to Exam Selection
          </button>
        </div>
      ) : (
        <div className="card shadow-lg p-4">
          <h4 className="card-title">
            Q{currentQuestion + 1}: {questions[currentQuestion]?.text || "Question not available"}
          </h4>

          <div className="mt-3">
            {questions[currentQuestion]?.options?.length > 0 ? (
              questions[currentQuestion].options.map((option, index) => (
                <div key={index} className="form-check">
                  <input
                    className="form-check-input"
                    type="radio"
                    name="questionOption"
                    id={`option-${index}`}
                    value={option}
                    checked={selectedOption === option}
                    onChange={() => handleOptionChange(option)}
                  />
                  <label className="form-check-label" htmlFor={`option-${index}`}>
                    {option}
                  </label>
                </div>
              ))
            ) : (
              <p className="text-danger">No options available for this question.</p>
            )}
          </div>

          <div className="mt-4 d-flex justify-content-between">
            <button className="btn btn-secondary" disabled={currentQuestion === 0} onClick={handlePreviousQuestion}>
              Previous
            </button>
            <button className="btn btn-primary" onClick={handleNextQuestion} disabled={currentQuestion >= questions.length - 1}>
              {currentQuestion === questions.length - 1 ? "Finish" : "Next"}
            </button>
          </div>
        </div>
      )}

      <div className="mt-4">
        <h5 className="text-danger">Alerts</h5>
        {alerts.length > 0 ? (
          alerts.map((alert, index) => (
            <div key={index} className="alert alert-danger">
              {alert.message}
            </div>
          ))
        ) : (
          <p>No alerts</p>
        )}
      </div>
    </div>
  );
};

export default ExamPage;
