import { useNavigate } from "react-router-dom";

function SelectRole() {
  const navigate = useNavigate();

  const handleRoleSelection = (role) => {
    // Send role to the backend or store it in local storage
    console.log("Selected role:", role);
    navigate(role === "admin" ? "/dashboard" : "/exam"); // Redirect after selection
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen">
      <h2 className="text-2xl font-bold">Select Your Role</h2>
      <button className="m-4 p-2 bg-blue-500 text-white" onClick={() => handleRoleSelection("admin")}>
        Admin
      </button>
      <button className="m-4 p-2 bg-green-500 text-white" onClick={() => handleRoleSelection("student")}>
        Student
      </button>
    </div>
  );
}

export default SelectRole;
