import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const ExamPage = () => {
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [answers, setAnswers] = useState({});
  const [timeLeft, setTimeLeft] = useState(3600); // 1-hour timer
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch questions from backend
    axios
      .get("http://localhost:3001/exam/questions", { withCredentials: true })
      .then((response) => setQuestions(response.data))
      .catch(() => navigate("/login"));
  }, [navigate]);

  useEffect(() => {
    // Timer countdown
    const timer = setInterval(() => {
      setTimeLeft((prevTime) => {
        if (prevTime <= 1) {
          clearInterval(timer);
          handleSubmit();
          return 0;
        }
        return prevTime - 1;
      });
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  const handleAnswerChange = (questionId, answer) => {
    setAnswers({ ...answers, [questionId]: answer });
  };

  const handleNext = () => {
    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    }
  };

  const handlePrev = () => {
    if (currentQuestionIndex > 0) {
      setCurrentQuestionIndex(currentQuestionIndex - 1);
    }
  };

  const handleSubmit = async () => {
    try {
      await axios.post("http://localhost:3001/exam/submit", { answers }, { withCredentials: true });
      navigate("/dashboard");
    } catch (error) {
      console.error("Error submitting exam", error);
    }
  };

  return (
    <div className="min-h-screen p-6 bg-gray-100 flex flex-col items-center">
      <div className="bg-white p-6 rounded-lg shadow-lg max-w-3xl w-full">
        <div className="flex justify-between items-center mb-4">
          <h1 className="text-2xl font-bold">Exam</h1>
          <p className="text-red-500 font-semibold">Time Left: {Math.floor(timeLeft / 60)}:{timeLeft % 60}</p>
        </div>
        {questions.length > 0 && (
          <div>
            <h2 className="text-lg font-semibold mb-4">{questions[currentQuestionIndex].question}</h2>
            {questions[currentQuestionIndex].options.map((option, index) => (
              <div key={index} className="mb-2">
                <input
                  type="radio"
                  name={`question-${currentQuestionIndex}`}
                  value={option}
                  checked={answers[questions[currentQuestionIndex].id] === option}
                  onChange={() => handleAnswerChange(questions[currentQuestionIndex].id, option)}
                />
                <label className="ml-2">{option}</label>
              </div>
            ))}
          </div>
        )}
        <div className="flex justify-between mt-6">
          <button onClick={handlePrev} disabled={currentQuestionIndex === 0} className="px-4 py-2 bg-gray-300 rounded">Previous</button>
          {currentQuestionIndex === questions.length - 1 ? (
            <button onClick={handleSubmit} className="px-4 py-2 bg-blue-500 text-white rounded">Submit</button>
          ) : (
            <button onClick={handleNext} className="px-4 py-2 bg-green-500 text-white rounded">Next</button>
          )}
        </div>
      </div>
    </div>
  );
};

export default ExamPage;
