import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import io from "socket.io-client";
import axios from "axios";

const socket = io("http://localhost:3001");

const ExamPage = ({ studentName, studentEmail, testName }) => {
  const { examId } = useParams(); // Exam ID from URL params
  const navigate = useNavigate();

  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [alerts, setAlerts] = useState([]);
  const [timer, setTimer] = useState(3600); // Default: 1 hour in seconds
  const [loading, setLoading] = useState(true);

  // Start the exam and track student
  useEffect(() => {
    const startExam = async () => {
      try {
        await axios.post("http://localhost:3001/start-exam", {
          studentName,
          studentEmail,
          testName,
          examId,
        });
      } catch (error) {
        console.error("Error starting exam:", error);
      }
    };

    startExam();

    // Connect socket for risk alerts
    socket.on("risk_alert", (alert) => {
      setAlerts((prevAlerts) => [...prevAlerts, alert]);
    });

    return () => {
      socket.off("risk_alert");
    };
  }, [studentName, studentEmail, testName, examId]);

  // Fetch questions for the exam
  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const response = await axios.get(
          `http://localhost:3001/exams/${examId}/questions`,
          { withCredentials: true }
        );
        setQuestions(response.data.questions);
        setLoading(false); // Questions loaded
      } catch (error) {
        console.error("Error fetching questions:", error);
        navigate("/exam-selection");
      }
    };

    fetchQuestions();
  }, [examId, navigate]);

  // Countdown timer
  useEffect(() => {
    const countdown = setInterval(() => {
      setTimer((prev) => (prev > 0 ? prev - 1 : 0));
    }, 1000);

    return () => clearInterval(countdown);
  }, []);

  // Send monitoring events to the server
  useEffect(() => {
    const handleMouseMove = (event) => {
      socket.emit("mouse_movement", {
        x: event.clientX,
        y: event.clientY,
        timestamp: Date.now(),
      });
    };

    const handleKeyPress = (event) => {
      socket.emit("keystroke_pattern", {
        key: event.key,
        timestamp: Date.now(),
      });
    };

    window.addEventListener("mousemove", handleMouseMove);
    window.addEventListener("keydown", handleKeyPress);

    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
      window.removeEventListener("keydown", handleKeyPress);
    };
  }, []);

  // Navigate to next question
  const handleNextQuestion = () => {
    setCurrentQuestion((prev) =>
      prev < questions.length - 1 ? prev + 1 : prev
    );
  };

  return (
    <div className="p-4">
      <header className="flex justify-between items-center">
        <h1>Exam: {testName}</h1>
        <span>{`Time Left: ${Math.floor(timer / 60)}:${timer % 60}`}</span>
      </header>

      {loading ? (
        <p>Loading questions...</p>
      ) : (
        <div className="mt-4">
          <h2>
            Q{currentQuestion + 1}: {questions[currentQuestion]?.text}
          </h2>
          {/* Add options here if questions include them */}
        </div>
      )}

      <div className="mt-4">
        <button
          onClick={handleNextQuestion}
          disabled={currentQuestion >= questions.length - 1}
        >
          Next
        </button>
      </div>

      <div className="mt-4 text-red-600">
        <h3>Alerts</h3>
        {alerts.map((alert, index) => (
          <p key={index}>{alert.message}</p>
        ))}
      </div>
    </div>
  );
};

export default ExamPage;
