import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import { io } from "socket.io-client"; // Import socket.io-client

const ExamPage = () => {
  const { examId } = useParams();
  const navigate = useNavigate();
  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const [answers, setAnswers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [timer, setTimer] = useState(3600); // 1 hour in seconds
  const [warningMessage, setWarningMessage] = useState(null); // For risk warnings
  const [socket, setSocket] = useState(null); // WebSocket connection

  // Establish WebSocket connection on component mount
  useEffect(() => {
    const newSocket = io("http://localhost:3001"); // Connect to WebSocket server
    setSocket(newSocket);

    newSocket.on("connect", () => {
      console.log("Connected to WebSocket server:", newSocket.id);
    });

    // Listen for risk warnings from the server
    newSocket.on("risk_warning", (data) => {
      setWarningMessage(data.message); // Set warning message
      setTimeout(() => setWarningMessage(null), 5000); // Clear warning after 5 seconds
    });

    newSocket.on("lock_exam", (data) => {
      alert(data.message); // Notify user of exam lock
      document.querySelectorAll("input, button").forEach((el) => {
        el.disabled = true; // Disable input and buttons
      });
    });

    return () => {
      newSocket.disconnect(); // Cleanup on component unmount
    };
  }, []);

  // Fetch questions for the exam
  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const response = await axios.get(`http://localhost:3001/exams/${examId}/questions`, {
          withCredentials: true,
        });
        setQuestions(response.data.questions);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching questions:", error.response?.data || error.message);
        setLoading(false);
        alert("Failed to load exam questions.");
      }
    };

    fetchQuestions();
  }, [examId]);

  // Timer functionality
  useEffect(() => {
    const countdown = setInterval(() => {
      setTimer((prev) => (prev > 0 ? prev - 1 : 0));
    }, 1000);

    return () => clearInterval(countdown); // Cleanup timer
  }, []);

  // Send mouse movement to the backend
  useEffect(() => {
    const handleMouseMove = (e) => {
      if (socket) {
        socket.emit("mouse_activity", { x: e.clientX, y: e.clientY, timestamp: Date.now() });
      }
    };
    window.addEventListener("mousemove", handleMouseMove);

    return () => window.removeEventListener("mousemove", handleMouseMove);
  }, [socket]);

  // Send keystroke activity to the backend
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (socket) {
        socket.emit("keystroke_activity", { key: e.key, timestamp: Date.now() });
      }
    };
    window.addEventListener("keydown", handleKeyDown);

    return () => window.removeEventListener("keydown", handleKeyDown);
  }, [socket]);

  // Send tab activity to the backend
  useEffect(() => {
    const handleVisibilityChange = () => {
      if (socket && document.visibilityState === "hidden") {
        socket.emit("tab_change", { action: "Tab switched", timestamp: Date.now() });
      }
    };
    document.addEventListener("visibilitychange", handleVisibilityChange);

    return () => document.removeEventListener("visibilitychange", handleVisibilityChange);
  }, [socket]);

  // Handle answer selection
  const handleOptionChange = (option) => {
    setSelectedOption(option);

    // Save the selected answer for the current question
    setAnswers((prev) => {
      const updatedAnswers = [...prev];
      updatedAnswers[currentQuestion] = { questionId: questions[currentQuestion].id, answer: option };
      return updatedAnswers;
    });
  };

  // Navigate to the next question or finish the exam
  const handleNextQuestion = () => {
    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
      setSelectedOption(null); // Reset selected option
    } else {
      // Submit the exam on completion
      handleSubmitExam();
    }
  };

  // Submit the exam to the backend
  const handleSubmitExam = async () => {
    try {
      const response = await axios.post(
        "http://localhost:3001/submit-exam",
        {
          examId,
          studentId: 1, // Replace with the actual student ID
          answers,
        },
        { withCredentials: true }
      );

      const results = response.data.results;
      alert(`Exam submitted! Your score: ${results.score}/${results.totalQuestions}`);
      navigate("/results", { state: { results } }); // Redirect to results page
    } catch (error) {
      console.error("Error submitting exam:", error.response?.data || error.message);
      alert("Failed to submit the exam. Please try again.");
    }
  };

  if (loading) {
    return <p>Loading questions...</p>;
  }

  if (questions.length === 0) {
    return <p>No questions available for this exam.</p>;
  }

  const currentQ = questions[currentQuestion]; // Get the current question safely

  return (
    <div className="container mt-5">
      {/* Warning banner for risk activity */}
      {warningMessage && (
        <div className="alert alert-warning text-center" role="alert">
          {warningMessage}
        </div>
      )}

      <header className="d-flex justify-content-between align-items-center mb-4">
        <h1 className="text-primary">Exam</h1>
        <div className="text-danger h4">
          Time Left: {Math.floor(timer / 60)}:{("0" + (timer % 60)).slice(-2)}
        </div>
      </header>

      {currentQ ? (
        <div className="card shadow-lg p-4">
          <h4 className="card-title">
            Q{currentQuestion + 1}: {currentQ.text}
          </h4>
          <div className="mt-3">
            {currentQ.options?.map((option, index) => (
              <div key={index} className="form-check">
                <input
                  className="form-check-input"
                  type="radio"
                  name={`question-${currentQuestion}`}
                  id={`option-${index}`}
                  value={option}
                  onChange={() => handleOptionChange(option)}
                  checked={selectedOption === option}
                />
                <label className="form-check-label" htmlFor={`option-${index}`}>
                  {option}
                </label>
              </div>
            ))}
          </div>
          <button
            className="btn btn-primary mt-4"
            onClick={handleNextQuestion}
            disabled={selectedOption === null}
          >
            {currentQuestion === questions.length - 1 ? "Finish Exam" : "Next Question"}
          </button>
        </div>
      ) : (
        <p>No more questions available.</p> // Fallback if currentQ is undefined
      )}
    </div>
  );
};

export default ExamPage;
